import React, { Component } from 'react';
import "./App.css";
import Header from "./components/Header";

class App extends Component {
  state = {
    "message": "Click an image to start",
    "score": 0,
    "images": [1, 2, 3, 4],
    "clicked": []
  };

  handleImageClickById = (imageID) => {
    let clicked = this.state.clicked.slice(0);

    //is this imageID in clicked
    if (clicked.findIndex(item => imageID === item) === -1) {
      clicked.push(imageID);
      this.setState({
        "clicked": clicked,
        "score": this.state.score + 1
      });

    } else {
      // you lose, start over
      this.setState({
        "message": "You lose, start over",
        "score": 0,
        "clicked": []
      });
    }
    // rearrange images
    let images = this.state.images.slice(0);
    images.sort(() => Math.random() - 0.5);
    this.setState({ images });
    console.log("handling click for ", imageID);
  }

  render() {
    return (
      <div className="App">
        <Header
          message={this.state.message}
          score={this.state.score}
          total={this.state.images.length} />
        <div id="board">
          {this.state.images.map(image => (
            <div className="image" id={"image-" + image} key={image} onClick={() => { this.handleImageClickById(image) }}>{image}</div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
